    private void SliderKgStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_SliderKgStateChanged

        weightValue.setText("Your weight(kg) is: "+SliderKg.getValue());
        CalculateBMI();
    }//GEN-LAST:event_SliderKgStateChanged

    private void SliderCmStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_SliderCmStateChanged
        heightValue.setText("Your height(cm) is: "+SliderCm.getValue());
        CalculateBMI();
    }//GEN-LAST:event_SliderCmStateChanged

