    private void CalculateBMI() { 
        double height = (double)SliderCm.getValue()/100d;
        double bmi = (double)SliderKg.getValue()/(height*height);
        double roundedBmi = Math.round(bmi * 100.0) / 100.0;
        String answer = "" + roundedBmi;
        jTextField3.setText(answer);
        
        if(bmi > 25.0){
            jTextField1.setText("Lose weight, fucking fat!");
        }
        else{
            jTextField1.setText("You're OK!");
        }
        
    }
